          helpText
        }) => !!helpText).map(({
          name,
          helpText
        }) => <Fragment key={name}>
                  <EuiTitle size="xxs">
                    <p>
                      <strong>{name}</strong>
                    </p>
                  </EuiTitle>
                  <EuiSpacer size="s" />
                  {helpText}
                  <EuiSpacer size="l" />
                </Fragment>)}
            <EuiHorizontalRule />
          </EuiModalBody>
          <EuiModalFooter>
            <EuiButton onClick={() => setIsShowingHelp(false)} fill>
